import React from "react";

// reactstrap components
import { Button, Card, Form, Input, Container, Row, Col } from "reactstrap";

// created classes
import Customer from "classes/Customer.js"

// Post Controller
import CustomerPOST from "controllers/CustomerPOST.js"

// Using ReactDOM
import ReactDOM from 'react-dom';

class PlayerForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            Customer: Customer
        };
    }

    componentWillMount() {
		fetch('http://localhost:5000/api/Customer', {
			method: 'POST',
			body: JSON.stringify(
			{	
                Customer
			}),
			headers: {
				"Content-type": "application/json; charset=UTF-8"
			}
		}).then(response => {
				return response.json()
			}).then(json => {
				this.setState({
					Customer:json
				});
			});
	}

    render() {
        return (
            <div
                className="page-header"
                style={{
                    backgroundImage: "url(" + require("assets/img/MTG/https___magic.wizards.com_sites_mtg_files_images_wallpaper_Back-to-Basics_UMA_1280x960_Wallpaper.jpg") + ")"
                }}
            >
                <div className="filter" />
                <Container>
                    <Row>
                        <Col className="ml-auto mr-auto" lg="4">
                            <Card className="card-register ml-auto mr-auto">
                                <h3 className="title mx-auto">Halt, PlanesWalker!</h3>
                                <h5 className="title mx-auto">Who goes there?</h5>
                                <Form className="register-form">
                                    <label>Full Name</label>
                                    <Input placeholder="FullName" type="text" value={this.state.custname}
                                        onChange={(event) => this.setState({custname : event.target.value})} />
                                    <label>Email</label>
                                    <Input placeholder="Email" type="text" value={this.state.email} 
                                        onChange={(event) => this.setState({email: event.target.value})}/>
                                    <label>Mobile Phone</label>
                                    <Input placeholder="MobilePhone" type="text" value={this.state.mobilephone}
                                        onChange={(event) => this.setState({mobilephone: event.target.value})} />
                                    <Button
                                        block className="btn-round"
                                        color="primary"
                                        onClick={() => ReactDOM.render(<CustomerPOST />, document.getElementById('root'))}
                                    >
                                        Register
          </Button>
                                </Form>
                            </Card>
                        </Col>
                    </Row>
                </Container>
                <div className="footer register-footer text-center">
                    <h6>
                        Â© {new Date().getFullYear()}, made by Oda-Sensei
  </h6>
                </div>
            </div>
        );
    }
}

export default PlayerForm
