import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { MDBContainer } from "mdbreact";
import "assets/css/scrollbar.css";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col
} from "reactstrap";

class Tables extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        Box: [],
      spotid: props.spotid,
      Wish: props.Wish
    };
  }

  componentDidMount() {
    // Make a request for a user with a given ID
    this.GetBoxes();
  }

  GetBoxes = () => {
     axios.get(`http://localhost:5000/api/Box/Spot/${this.state.spotid}`)
       .then((response) => {
         this.setState({ Box: response.data });
       })
  };

    LoadBox = (e, itemIndex) => {
        this.setState({
            Box: this.state.Box.map((item, index) => {
                if (itemIndex === index) {
                    return {
                        ...item
                    };
                }
                return item;
            })
        })
        e.preventDefault();
        const boxid = this.state.Box.boxid
        axios.post(`http://localhost:5000/api/BoxContent/${boxid}`),
            {
                data: this.state.Wish
            }
        this.props.history.push("/mtgspotlanding")
    };  

    EmptyBox = (e, itemIndex) => {
        this.setState({
            Box: this.state.Box.map((item, index) => {
                if (itemIndex === index) {
                    return {
                        ...item
                    };
                }
                return item;
            })
        })
        e.preventDefault();
        const boxid = this.state.Box.boxid
        axios.delete(`http://localhost:5000/api/BoxContent/${boxid}`)
        this.props.history.push("/mtgspotlanding")
    };  

  rendertable = () => {
    return this.state.Box.map((item, index) => {
      return (
        <tr key={item.id}>
          <td>{item.boxnumber}</td>
          <td>{item.spotname}</td>
          <td>{item.custname}</td>
          <td>{item.ownername}</td>
          <td>{item.shelfid}</td>
          <td>{item.itemdescription}</td>
          <td>{item.quantity}</td>
          <td>
            <Button
              className="btn-round"
              color="info"
              outline
              size="sm"
              onClick={event => this.LoadBox(event, index)}
            >
              <i className="nc-icon nc-key-25" /> Load This Box
            </Button>
          </td>
          <td>
            <Button
              className="btn-round"
              color="info"
              outline
              size="sm"
              onClick={event => this.EmptyBox(event, index)}
            >
              <i className="nc-icon nc-lock-circle-open" /> Drain This Box
            </Button>
          </td>
        </tr>
      );
    });
  };

  render() {
    let pageHeader = React.createRef();
    const scrollContainerStyle = { width: "1000px", maxHeight: "450px" };

    return (
      <>
        <div
          style={{
            backgroundImage:
              "url(" +
              require("assets/img/MTG/https___magic.wizards.com_sites_mtg_files_images_wallpaper_Approach-of-the-Second-Sun_AKH_1280x960_Wallpaper.jpg") +
              ")"
          }}
          className="page-header"
          data-parallax={true}
          ref={pageHeader}
        >
          <div className="filter" />
          <MDBContainer>
            <div
              className="scrollbar scrollbar-default"
              style={scrollContainerStyle}
            >
              <Row md="12">
                <Col md="12">
                  <Card>
                    <CardHeader>
                      <CardTitle tag="h4">Fetch a Box.</CardTitle>
                    </CardHeader>
                    <CardBody>
                      <Table responsive>
                        <thead className="text-primary">
                          <tr>
                            <th>Box Number</th>  
                            <th>Spot Name</th>
                            <th>Customer Name</th>
                            <th>Owner Name</th>
                            <th>Shelf #</th>
                            <th>Card Name</th>
                            <th>Quantity</th>
                          </tr>
                        </thead>
                        <tbody>{this.rendertable()}</tbody>
                      </Table>
                      <Link to = "/MTGSpotLanding">
                      <Button
                        className="btn-round"
                        color="primary"
                      >
                        Return
                      </Button>
                      </Link>
                    </CardBody>
                  </Card>
                </Col>
              </Row>
            </div>
          </MDBContainer>
        </div>
      </>
    );
  }
}

export default Tables;
